    private void btnSalvarCatActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvarCatActionPerformed
        Categoria cat = new Categoria();
        cat.setNome(txtNomeCategoria.getText());
        boolean funcionou = CategoriaDAO.inserir(cat);
        if (funcionou){
            txtNomeCategoria.setText("");
        }

    }//GEN-LAST:event_btnSalvarCatActionPerformed


